    public void LogADM(){
        String query;
        query = "SELECT * FROM siapa WHERE "
                + "user = ? AND password = ?";
        PreparedStatement statement;
        Connection connection = jaringan.ambilKoneksi();
        rumah baru = new rumah();
        try {
            statement = connection.prepareStatement(query);
            statement.setString(1, jTextField1.getText());
            statement.setString(2, jPasswordField1.getText());
            ResultSet rs = statement.executeQuery();
            if (rs.next()){
                new rumah ().setVisible(true);
                dispose();
            }else{
                jLabel4.setText("Username atau Password anda salah");
            }
        } catch (SQLException e) {}
    }

